mapper.select:
SELECT REPORT_NO, REPORT_SENID, REPORT_REID, REPORT_TITLE, REPORT_CONTENT
FROM REPORT
WHERE REPORT_NO = #{report_no}

SELECT * 
	FROM (
		SELECT ROWNUM RN, A.* 
			FROM (
				SELECT * 
				FROM MEMBER 
				ORDER BY MEMBER_ENABLED DESC 
					) A
		)
WHERE RN BETWEEN #{start} AND #{end}
